def sendMsg(msg):
    # 定义要发送的消息内容
    x = len(msg)
    data = {
        "msgtype": "markdown",
        "markdown": {
            "content": f" 今日检测到<font color=\"warning\">{x}</font>个单位机关简介超半年未更新，请及时更新\n\n"
        }
    }

    # 添加每个单位的信息
    for key, value in msg.items():
        data["markdown"]["content"] += f"## {key}\n\n"
        for menu, href in value.items():
            data["markdown"]["content"] += f"- {menu}: {cacul(href)}天未更新\n"

    # 定义企业微信机器人的webhook地址
    url = "https://qyapi.weixin.qq.com/cgi-bin/webhook/send?key=425a53f6-696e-46c1-9d4a-fae8940b136f"

    # 发送HTTP POST请求
    response = requests.post(url, data=json.dumps(data))

    # 输出响应结果
    print(response.text)

